-- Create tables 
 
CREATE TABLE IF NOT EXISTS settings
(
    settings_id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
    name VARCHAR(25) NOT NULL,
    description VARCHAR(100),
    value VARCHAR(100)
);

CREATE TABLE IF NOT EXISTS dbg_log
(
    source_element VARCHAR(100),
    log_timestamp DATETIME,
    log_level VARCHAR(25),
    msg TEXT,
    add_description TEXT,
    stack_trace TEXT
);

CREATE TABLE IF NOT EXISTS translation 
(
    translation_id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
    context VARCHAR(75) NOT NULL, -- Name of UI element or module
    english TEXT NOT NULL,
    german TEXT,
    russian TEXT,
    spanish TEXT,
    portuguese TEXT,
    italian TEXT,
    french TEXT,
    ukranian TEXT,
    dutch TEXT,
    polish TEXT,
    czech TEXT,
    serbian TEXT,
    croatian TEXT,
    swedish TEXT,
    norwegian TEXT,
    danish TEXT,
    afrikaans TEXT,
    turkish TEXT,
    kazakh TEXT,
    armenian TEXT,
    georgian TEXT,
    romanian TEXT,
    bulgarian TEXT,
    albanian TEXT,
    greek TEXT,
    indonesian TEXT,
    malay TEXT,
